var Task_Reboot = global.get('DDC2.Tasks.Task_Reboot')
if (Task_Reboot.TASK_REBOOT) {
    msg.payload = {
        offtime: Task_Reboot.Restart_Time,
        mon: Task_Reboot.Monday,
        tue: Task_Reboot.Tuesday,
        wed: Task_Reboot.Wednesday,
        thu: Task_Reboot.Thursday,
        fri: Task_Reboot.Friday,
        sat: Task_Reboot.Saturday,
        sun: Task_Reboot.Sunday
    }
    return [msg,msg]
} else {
    return [null,msg]
}

var DDC2 = flow.get("$parent.DDC2")
//var DDC2 = flow.get("DDC2")
var Task_Reboot = global.get('DDC2.Tasks.Task_Reboot')
var status_colour = "grey"
var status_shape = "dot"
var status_text = "NO SCHEDULED REBOOT SET"
if (DDC2.ServerInfo.ServerID == Task_Reboot.DDC2_MASTER) {
    //Means this flow is master
    status_colour = "green"
    status_text = 'MASTER: '
} else {
    //Means this flow is a slave
    status_colour = "yellow"
    status_text = 'SLAVE: '
}
if (msg.payload.off == "suspended") {
    status_colour = "grey"
    status_text = status_text + 'NO REBOOT SET'
} else {
    var next_reboot_raw = Date.parse(msg.payload.off)
    var formatted_reboot = new Date(next_reboot_raw).toLocaleString(DDC2.ServerInfo.Language, { hour12: false })
    //node.warn("formatted_reboot: " + formatted_reboot);
    status_text = status_text + formatted_reboot
}

//node.status({ fill: status_colour, shape: status_shape, text: status_text });
msg.payload = {
    fill: status_colour,
    shape: status_shape,
    text: status_text
}
return msg;

// This bit of code requests data from powershell to populate 
// the configuration items that were previously in Node-Red.
var payload = "-init";  
msg.payload = payload;
var command = "!init";
var requesttype = command.replace('!','')
var access = "GRANTED";
msg.data = {
    "createdTimestamp": Date.now()
    }
var tmstmp = msg.data.createdTimestamp;
var localdatetime = (new Date(tmstmp)).toLocaleString();

msg.ddc2 = {
          "request" : command,
    "FormattedTime" : localdatetime,
   "RequestChannel" : "",
 "RequestChannelID" : "",
      "requesttype" : requesttype,
           "access" : access,
       "authorized" : true,
            "valid" : true,
          "comment" : "",
     "Notification" : "",
              "TTC" : "", //Time To Completion, This measures the time the system recieved the request through to the time the request was completed. This item is used in further down in the flow
              "raw" : msg.payload
}
node.status({fill:"green",shape:"dot",text:"DDC2 Initialized"});
return msg;

var payload = "-AutoStart"; //Changed while testing (Switch between -status or -doupdate)
msg.payload = payload;
var DDC2 = flow.get('DDC2');
msg.author = {
        "username": DDC2.ServerInfo.ServerID,
        "id": DDC2.ServerInfo.ServerID
    }

msg.data = {
    "createdTimestamp": Date.now()
    }
msg.channel = {
    "id": DDC2.Channel.Admin
    }

var tmstmp = msg.data.createdTimestamp;
var localdatetime = (new Date(tmstmp)).toLocaleString();
var command = "!autostart";
var requesttype = command.replace('!','')
var access = "GRANTED";

msg.ddc2 = {
          "request" : command,
    "FormattedTime" : localdatetime,
   "RequestChannel" : "NOT APPLICABLE",
 "RequestChannelID" : "NOT APPLICABLE",
      "requesttype" : requesttype,
           "access" : access,
       "authorized" : true,
            "valid" : true,
          "comment" : "Server AutoStart",
     "Notification" : DDC2.ServerInfo.ServerID+ ` has initialized its ` +requesttype+ ` sequence on the ` +localdatetime,
              "raw" : msg.payload
}

return msg

//DO NOT MODIFY THIS DATA!!
var Func = flow.get('Func'); //Loads Flow Functions into memory
//This sets the entire DDC2 variable structure from the ddc2_config.ps1, dont remove it!
flow.set('DDC2', 0);
msg.PS.TMP = {
    'GuildIDPwd' : Func.genPassword(7),
  'SrvStatusPwd' : Func.genPassword(7)
}
flow.set('DDC2', msg.PS);

if ((msg.PS.ServerInfo.DDC2_MASTER) && (msg.PS.ServerInfo.Task_Reboot.TASK_REBOOT)){
  global.set('DDC2.Tasks.Task_Reboot', 0)
  global.set('DDC2.Tasks.Task_Reboot', msg.PS.ServerInfo.Task_Reboot)
}

var TimeDate = new Date(msg.data.createdTimestamp).toLocaleString();
node.status({fill:"green",shape:"dot",text:TimeDate});
return msg;

flow.set('DDC2.Ready', true);
node.status({fill:"green",shape:"dot",text:"SERVER READY!"});

//This line checks if the refresh command has been called, if so it does not execute the Autostart sequence.
if(msg.ddc2.requesttype == "refresh") {} else {return msg}

/*
Checks the Discord Message User groups against the specific command being run and either passes the message onto Pre-Processing or sends an 
access denied message and logs the access denied request. Note, Access Granted is assumed from this point so the Pre-Processing section needs 
to take care of the task notification after the command in questions syntax is checked.
*/


////////////////////////////////////////////////////////////////////////////////////////////////////
////Initialize Variables
var MsgSent = context.get('MsgSent') || 1;

//Get Flow Variables
var Func = flow.get('Func');
var DDC2 = flow.get('DDC2');
var CommandPermissions = flow.get('DDC2.Permissions.'+msg.ddc2.requesttype)


if (typeof CommandPermissions === 'undefined') {
    msg.ddc2.authorized = false;
    msg.ddc2.Notification = `Attention: ` + msg.author.username + `, your ` + (Func.Capitalize(msg.ddc2.requesttype)) + ` on ` + DDC2.ServerInfo.ServerID +  ` is not a valid command, for further assistance please consult the help command by typing ` + DDC2.ServerInfo.CommandPrefix + `help`
    msg.ddc2.access = "INVALID"
    node.status({ fill: "red", shape: "ring", text: "INVALID:::" + MsgSent });
    context.set('MsgSent', (MsgSent + 1));
} else {
    if (Func.GrantAccess(CommandPermissions,msg.member.roles)) {
        msg.ddc2.authorized = true;
        msg.ddc2.access = "GRANTED"
        }
    else {
        msg.ddc2.authorized = false;
        msg.ddc2.Notification = `!!ACCESS DENIED!! ` +msg.author.username+ ` you do not have access to the `+(Func.Capitalize(msg.ddc2.requesttype))+` command on server ` +DDC2.ServerInfo.ServerID+ `, message ignored on the `+msg.ddc2.FormattedTime
        msg.ddc2.access = "DENIED"
        node.status({fill:"red",shape:"dot",text: "DENIED:::"+MsgSent});
        context.set('MsgSent', (MsgSent + 1));
    }
}
if(msg.ddc2.authorized) {return [null,msg]} else {return [msg,null]}

////Initialize Variables
var Func = flow.get('Func');
var DDC2 = flow.get('DDC2');
////////////////////////////////////////////////////////////////////////////////////////////////////
var ttc = Func.DaysBetween((new Date(msg.data.createdTimestamp)), new Date());
msg.ddc2.TTC = ttc
msg.ddc2.comment = "Request Actioned"
var extraP = 'p'
if(msg.ddc2.requesttype != 'stop') {extraP = ''}

msg.PS.Server.UpTimeStr = Func.pad(msg.PS.Server.UpTime.Days,2)+ `:`+Func.pad(msg.PS.Server.UpTime.Hours,2)+`:`+Func.pad(msg.PS.Server.UpTime.Minutes,2)+`:`+Func.pad(msg.PS.Server.UpTime.Seconds,2)

if(msg.PS.DCS.IsActive === true) {
    msg.PS.DCS.RunTimeStr = Func.pad(msg.PS.DCS.RunTime.Days,2)+ `:`+Func.pad(msg.PS.DCS.RunTime.Hours,2)+`:`+Func.pad(msg.PS.DCS.RunTime.Minutes,2)+`:`+Func.pad(msg.PS.DCS.RunTime.Seconds,2)
    msg.PS.DCS.PriorityStr = Func.GetPriority(msg.PS.DCS.PriorityClass)
}
if(msg.PS.SRS.IsActive === true) {
    msg.PS.SRS.RunTimeStr = Func.pad(msg.PS.SRS.RunTime.Days,2)+ `:`+Func.pad(msg.PS.SRS.RunTime.Hours,2)+`:`+Func.pad(msg.PS.SRS.RunTime.Minutes,2)+`:`+Func.pad(msg.PS.SRS.RunTime.Seconds,2)
    msg.PS.SRS.PriorityStr = Func.GetPriority(msg.PS.SRS.PriorityClass)
}
msg.ddc2.Notification = DDC2.ServerInfo.ServerID+ ` has `+(Func.Capitalize(msg.ddc2.requesttype))+extraP+`ed the server. The task took `+msg.ddc2.TTC+` to complete.`
////////////////////////////////////////////////////////////////////////////////////////////////////
return msg

////Initialize Variables
var Func = flow.get('Func');
var DDC2 = flow.get('DDC2');
////////////////////////////////////////////////////////////////////////////////////////////////////
var ttc = Func.DaysBetween((new Date(msg.data.createdTimestamp)), new Date());
msg.ddc2.TTC = ttc
msg.ddc2.comment = "Request Actioned"
msg.ddc2.Notification = DDC2.ServerInfo.ServerID+ ` is wondering how you are seeing this message if the server just restarted before it had a chance to send this message. Will wonders never cease! The task took `+msg.ddc2.TTC+` to complete.`
////////////////////////////////////////////////////////////////////////////////////////////////////
return msg

var MsgSent = context.get('MsgSent') || 1;
var DDC2 = flow.get('DDC2');
var Func = flow.get('Func');
msg.ddc2.Notification = "" //Stops the Notification system from sending out a message.
msg.ddc2.comment = "Cached Response"
msg.ddc2.info = {
    "ShowBluPWD" : DDC2.ServerInfo.ShowBluPWD,
    "ShowRedPWD" : DDC2.ServerInfo.ShowRedPWD,
    "ShowSrvPWD" : DDC2.ServerInfo.ShowSrvPWD,
    "ShowLotATC" : DDC2.ServerInfo.ShowLotATC,
    "ShowTACView" : DDC2.ServerInfo.ShowTACView
    }

node.status({fill:"green",shape:"ring",text: "IP's misplaced: "+MsgSent});
if(msg.channel.id == DDC2.Channel.Admin) {
//Show everything

//DDC2.AppConfig.DCS.password = "chickenFucker"
msg.ddc2.info.ShowBluPWD = true
msg.ddc2.info.ShowRedPWD = true
msg.ddc2.info.ShowSrvPWD = true
msg.ddc2.info.ShowLotATC = true
msg.ddc2.info.ShowTACView = true
context.set('MsgSent', (MsgSent + 1));
return msg;
}

if(msg.channel.id == DDC2.Channel.Red) {
//Set Password to RED
msg.ddc2.info.ShowBluPWD = false
context.set('MsgSent', (MsgSent + 1));
return msg;
}

if(msg.channel.id == DDC2.Channel.Blue) {
//Set Password to RED
msg.ddc2.info.ShowRedPWD = false
context.set('MsgSent', (MsgSent + 1));
return msg;
}

if(msg.channel.id == DDC2.Channel.Support) {
msg.ddc2.info.ShowBluPWD = false
msg.ddc2.info.ShowRedPWD = false
context.set('MsgSent', (MsgSent + 1));
return msg;
}

var HelpEnabled = flow.get('DDC2.ServerInfo.ENABLE_HELP');
if (HelpEnabled) {
    node.status({fill:"green",shape:"dot",text:"Help Enabled"});
    }
else {
    node.status({fill:"green",shape:"ring",text:"Help Disabled"});
    }

