    GrantAccess: function (haystack, arr) {
        return arr.some(function (v) {
            return haystack.indexOf(v) >= 0;
        })
    },
    pad: function (num, size) {
        var s = num + "";
        while (s.length < size) s = "0" + s;
        return s;
    },
    FormatDateTime: function (local_dateVar, langCode) {
        var date = new Date(local_dateVar * 1000)
        var day = date.toLocaleString(langCode, { day: '2-digit' });   // DD
        day = Func.pad(day, 2)
        var month = date.toLocaleString(langCode, { month: 'short' }); // MMM
        var year = date.toLocaleString(langCode, { year: 'numeric' }); // YYYY
        var hour = date.toLocaleString(langCode, { hour: '2-digit', hour12: false }); // HH)
        if (hour == 24) { hour = 0 }
        hour = Func.pad(hour, 2)
        var minute = date.toLocaleString(langCode, { minute: '2-digit' }); // MM)
        minute = Func.pad(minute, 2)
        var second = date.toLocaleString(langCode, { second: '2-digit' }); // SS)
        second = Func.pad(second, 2)
        return `${day} ${month} ${year}, ${hour}:${minute}:${second}`;
    },
    DaysBetween: function (date1, date2) {
        var one_day = 1000 * 60 * 60 * 24;
        var date1_ms = date1.getTime();
        var date2_ms = date2.getTime();
        var difference_ms = date2_ms - date1_ms;
        difference_ms = Math.round(difference_ms / 1000, 3);
        var mseconds = difference_ms % 1000;
        var seconds = Math.floor(difference_ms % 60);
        difference_ms = difference_ms / 60;
        var minutes = Math.floor(difference_ms % 60);
        difference_ms = difference_ms / 60;
        var hours = Math.floor(difference_ms % 24);
        var days = Math.floor(difference_ms / 24);
        return Func.pad(minutes, 2) + ':' + Func.pad(seconds, 2) + '.' + Func.pad(mseconds, 3);
    },
    GetPriority: function (priority) {
        var PriorityTable = {
            64: "Low",
            16384: "Below Normal",
            32: "Normal",
            32768: "Above Normal",
            128: "High",
            256: "Realtime"
        };
        return (PriorityTable[priority])
    },
    Capitalize: function (str) {
        const capitalized = str.charAt(0).toUpperCase() + str.slice(1);
        return capitalized;
    },
    GetCoalition: function (coal) {
        var CoalitionTable = {
            0: "Spectators",
            1: "Red Forces",
            2: "Blue Forces"
        };
        return (CoalitionTable[coal])
    },
