        return /^(([0-9]|[1-9][0-9]|1[0-9]{2}|2[0-4][0-9]|25[0-5])\.){3}([0-9]|[1-9][0-9]|1[0-9]{2}|2[0-4][0-9]|25[0-5])$|^(([a-zA-Z]|[a-zA-Z][a-zA-Z0-9\-]*[a-zA-Z0-9])\.)*([A-Za-z]|[A-Za-z][A-Za-z0-9\-]*[A-Za-z0-9])$|^\s*((([0-9A-Fa-f]{1,4}:){7}([0-9A-Fa-f]{1,4}|:))|(([0-9A-Fa-f]{1,4}:){6}(:[0-9A-Fa-f]{1,4}|((25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)(\.(25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)){3})|:))|(([0-9A-Fa-f]{1,4}:){5}(((:[0-9A-Fa-f]{1,4}){1,2})|:((25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)(\.(25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)){3})|:))|(([0-9A-Fa-f]{1,4}:){4}(((:[0-9A-Fa-f]{1,4}){1,3})|((:[0-9A-Fa-f]{1,4})?:((25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)(\.(25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)){3}))|:))|(([0-9A-Fa-f]{1,4}:){3}(((:[0-9A-Fa-f]{1,4}){1,4})|((:[0-9A-Fa-f]{1,4}){0,2}:((25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)(\.(25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)){3}))|:))|(([0-9A-Fa-f]{1,4}:){2}(((:[0-9A-Fa-f]{1,4}){1,5})|((:[0-9A-Fa-f]{1,4}){0,3}:((25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)(\.(25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)){3}))|:))|(([0-9A-Fa-f]{1,4}:){1}(((:[0-9A-Fa-f]{1,4}){1,6})|((:[0-9A-Fa-f]{1,4}){0,4}:((25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)(\.(25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)){3}))|:))|(:(((:[0-9A-Fa-f]{1,4}){1,7})|((:[0-9A-Fa-f]{1,4}){0,5}:((25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)(\.(25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)){3}))|:)))(%.+)?\s*$/.test(IPAddr);
    },
    TimeOfDay: function (seconds) {
        const DaySeconds = seconds % 86400
        const format = val => `0${Math.floor(val)}`.slice(-2)
        const hours = DaySeconds / 3600
        const minutes = (DaySeconds % 3600) / 60
        return [hours, minutes, DaySeconds % 60].map(format).join(':')
    },
    FormatTime: function (seconds) {
        const format = val => `0${Math.floor(val)}`.slice(-2)
        const hours = seconds / 3600
        const minutes = (seconds % 3600) / 60
        return [hours, minutes, seconds % 60].map(format).join(':')
    },
    TimeDelta: function (d1, d2, OutputWords, ShowMS) {
        // TimeDelta(<date1>,<date2>,<output words true/false> , <show milliseconds true/false>)
        // Example: var td = TimeDelta(<date1>,<date2>,<output words true/false> , <show milliseconds true/false>)
        // Options for OutputType are "WORDS" and "NUMB" (Technically anything other than "WORDS" will yield the numbers output)
        // WORDS will output something like 12 hours 53 minutes 02 seconds
        // NUMBERS will output 12:53:02
        // The output is smart, it will only output the values it needs, so you will not get 00:00:00:01 for 1 second.
        const d = " day "
        const d_s = " days "
        const h = " hour "
        const h_s = " hours "
        const m = " minute "
        const m_s = " minutes "
        const s = " second "
        const s_s = " seconds "
        const ms = " millisecond "
        const ms_s = " milliseconds "
        const day_ms = 86400000 // Milliseconds in 1 day
        const hour_ms = 3600000 // Milliseconds in 1 hour
        const min_ms = 60000 // Milliseconds in 1 minute
        const sec_ms = 1000 // Milliseconds in 1 second
        //////////////////////////////////////////////////////////////
        var d1_ms = new Date(d1).getTime(); //Less recent time
        var d2_ms = new Date(d2).getTime(); //More recent time
        var OutputWords = OutputWords
        var ShowMS = ShowMS
        //////////////////////////////////////////////////////////////
        var delta_ms = Math.abs(d2_ms - d1_ms);  //Delta in ms
        //////////////////////////////////////////////////////////////
        var days = Func.pad(Math.floor(delta_ms / day_ms), 2)
        var hours = Func.pad(Math.floor((delta_ms % day_ms) / hour_ms), 2)
        var mins = Func.pad(Math.floor((delta_ms % hour_ms) / min_ms), 2)
        var secs = Func.pad(Math.floor((delta_ms % min_ms) / sec_ms), 2)
        var mils = Func.pad(Math.floor(delta_ms % sec_ms), 3)
        //////////////////////////////////////////////////////////////
        var rtn = ""
        if (OutputWords) {
            if (days <= 0) { days = 0 } else {
                if (days == 1) {
                    rtn = days + d
                } else {
                    rtn = days + d_s
                }
            }
            if (hours <= 0) { hours = 0 } else {
                if (hours == 1) {
                    rtn = rtn + hours + h
                } else {
                    rtn = rtn + hours + h_s
                }
            }
            if (mins <= 0) { mins = 0 } else {
                if (mins == 1) {
                    rtn = rtn + mins + m
                } else {
                    rtn = rtn + mins + m_s
                }
            }
            if (secs <= 0) { secs = 0 } else {
                if (secs == 1) {
                    rtn = rtn + secs + s
                } else {
                    rtn = rtn + secs + s_s
                }
            }
            if (ShowMS) {
                if (mils <= 0) { mils = 0 } else {
                    if (mils == 1) {
                        rtn = rtn + mils + ms
                    } else {
                        rtn = rtn + mils + ms_s
                    }
                }
            }
        } else {
            rtn = [days, hours, mins, secs].join(':')
            if (days <= 0) { rtn = [hours, mins, secs].join(':') }
            if (hours <= 0) { rtn = [mins, secs].join(':') }
            if (ShowMS) { rtn = [rtn, mils].join('.') }
        }
        return rtn
    },
    genPassword: function (passwordLength) {
        //var chars = "0123456789abcdefghijklmnopqrstuvwxyz!@#$%^&*()ABCDEFGHIJKLMNOPQRSTUVWXYZ";
        var chars = "0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ";
        if (typeof passwordLength === 'undefined') {
            passwordLength = 8;
        }
        passwordLength--;
        var password = "";
        for (var i = 0; i <= passwordLength; i++) {
            var randomNumber = Math.floor(Math.random() * chars.length);
            password += chars.substring(randomNumber, randomNumber + 1);
        }
        return password
    }
}
flow.set('Func', Func);
//global.set('Func', Func);
node.status({
    fill: "green",
    shape: "dot",
    text: "Functions Loaded"
});

