// @ts-nocheck
////Initialize Variables
var Func = flow.get('Func');
var DDC2 = flow.get('DDC2');
var MsgSent = context.get('MsgSent') || 1;
////////////////////////////////////////////////////////////////////////////////////////////////////
msg.ddc2.Notification = ""
var msglow = msg.ddc2.raw.toLowerCase();
var InvalidFreq = false
var InvalidCoal = false
var InvalidMsg = false
var MessageFailed = false
var RadioRequest = ""
////Frequency
var Freq = ""
var FreqStart = msglow.indexOf('-freq ')
if(FreqStart == -1) {
    Freq = parseFloat(DDC2.AppConfig.SRS.DDC2_FREQ)
} else {
    var FreqAdd = FreqStart + 6
    var FreqStr = msglow.slice(FreqAdd)
    var FreqEnd = FreqStr.indexOf(' ')
    Freq = parseFloat(FreqStr.slice(0, FreqEnd))
    if((Freq < parseFloat(DDC2.AppConfig.SRS.DDC2_LOW)) || (Freq > parseFloat(DDC2.AppConfig.SRS.DDC2_HIGH))) {
        InvalidFreq = true
    }
}
////Coalition
var Coal = ""
var CoalStart = msglow.indexOf('-coal ')

if(CoalStart == -1) {
    Coal = parseInt(DDC2.AppConfig.SRS.DDC2_COAL)
    if(msg.data.channel.id == DDC2.Channel.Blue) {Coal = 2}
    if(msg.data.channel.id == DDC2.Channel.Red) {Coal = 1}
} else {
    var CoalAdd = CoalStart + 6
    var CoalStr = msglow.slice(CoalAdd)
    var CoalEnd = CoalStr.indexOf(' ')
    Coal = parseInt(CoalStr.slice(0, CoalEnd))
    if((Coal < 0) || (Coal > 2)) {
        InvalidCoal = true
    }
}
////Message
var Text2Radio = ""
var T2RStart = msglow.indexOf('-msg "')
if(T2RStart == -1) {
    InvalidMsg = true
} else {
    var T2RAdd = T2RStart + 6
    var T2RStr = msglow.slice(T2RAdd)
    var T2REnd = T2RStr.indexOf('"')
    var Unclean = T2RStr.slice(0, T2REnd)
    var cleanString = Unclean.replace(/[|&;$%@"<>*^-_=+;\\()+]/g, "");
    var WordCount = cleanString.split(' ').length
    var SpacesRemoved = cleanString.replace(/\s/g, '').length
    if((WordCount > 0) && (SpacesRemoved > 0)) {
        Text2Radio = cleanString
    } else {
        InvalidMsg = true
    }
}

var CoalitionName = Func.GetCoalition(Coal)

////Powershell Payload Prep
if((InvalidFreq) || (InvalidCoal) || (InvalidMsg)) {
    msg.ddc2.valid = false;
    MessageFailed = true
} else {
    msg.payload = `-radio -RadioFrq `+Freq+` -RadioUser `+msg.member.user.username+` -RadioSide `+Coal+` -RadioMSG "`+Text2Radio+`"`
    msg.ddc2.comment = "Passed to PS: "+msg.payload
    msg.ddc2.valid = true;
}

msg.ddc2.Radio = {
     "RadioUpper" : Func.Capitalize(msg.ddc2.requesttype),
           "Freq" : Freq,
           "Coal" : Coal,
  "CoalitionName" : CoalitionName,
     "Test2Radio" : Text2Radio,
    "InvalidFreq" : InvalidFreq,
    "InvalidCoal" : InvalidCoal,
     "InvalidMsg" : InvalidMsg,
  "MessageFailed" : MessageFailed
}


////////////////////////////////////////////////////////////////////////////////////////////////////
node.status({fill:"green",shape:"ring",text: "Ear holes molested: "+MsgSent});
context.set('MsgSent', (MsgSent + 1));
return msg

