func:
/*
Stop Command
usage

!stop <serverID> //DEFAULT ACTION: Stops DCS and SRS processes (Same as -Game)

!stop <serverID> -All  //Stops All DCS and Update processes

!stop <serverID> -Game //Stops DCS and SRS processes

!stop <serverID> -DCS //Stops DCS process

!stop <serverID> -SRS //Stops SRS process

!stop <serverID> -Update //Stops Update process

Options for Default are 

-StopAll
-StopGame
-StopDCS
-StopSRS
-StopUpdate

*/
////Initialize Variables
var Func = flow.get('Func');
var DDC2 = flow.get('DDC2');
////////////////////////////////////////////////////////////////////////////////////////////////////
var MsgSent = context.get('MsgSent') || 1;
node.status({fill:"green",shape:"ring",text: "Airbrakes Deployed : "+MsgSent});
context.set('MsgSent', (MsgSent + 1));
////////////////////////////////////////////////////////////////////////////////////////////////////
msg.ddc2.Stop = {
         "Game" : false,
          "All" : false,
          "DCS" : false,
          "SRS" : false,
       "Update" : false
    }
////////////////////////////////////////////////////////////////////////////////////////////////////
msg.ddc2.valid = true;
var msglow = msg.ddc2.raw.toLowerCase();
////////////////////////////////////////////////////////////////////////////////////////////////////
//DEFAULT ACTION (will be executed is no other switches are detected during message analysis)
var DefaultNote = msg.ddc2.Notification = DDC2.ServerInfo.ServerID+ ` has recieved a `+(Func.Capitalize(msg.ddc2.requesttype))+ ` command request from `+msg.author.username+ `. Please wait while this task is processed....`
var DefaultPayload = '-'+msg.ddc2.requesttype+" -StopGame" //DEFAULT SETTING IS -StopGame
////////////////////////////////////////////////////////////////////////////////////////////////////

var StopAll = msglow.indexOf('-all')
var doStopAll = ""
if(StopAll != -1) {
    doStopAll = "-StopAll"
    msg.ddc2.Stop.All = true
    msg.ddc2.Notification = DDC2.ServerInfo.ServerID+ ` has recieved a `+(Func.Capitalize(msg.ddc2.requesttype))+ ` -all command request from `+msg.author.username+ `. Please wait while this task is processed....`
    msg.payload = '-'+msg.ddc2.requesttype+" "+doStopAll
    return msg
}
////////////////////////////////////////////////////////////////////////////////////////////////////
var StopGame = msglow.indexOf('-game')
var doStopGame = ""
if(StopGame != -1) {
    doStopGame = "-StopGame"
    msg.ddc2.Stop.Game = true
    msg.ddc2.Notification = DDC2.ServerInfo.ServerID+ ` has recieved a `+(Func.Capitalize(msg.ddc2.requesttype))+ ` -Game command request from `+msg.author.username+ `. Please wait while this task is processed....`
    msg.payload = '-'+msg.ddc2.requesttype+" "+doStopGame
    return msg
}
////////////////////////////////////////////////////////////////////////////////////////////////////
var StopUpdate = msglow.indexOf('-update')
var doStopUpdate = ""
if(StopUpdate != -1) {
    doStopUpdate = "-StopUpdate"
    msg.ddc2.Stop.Update = true
    msg.ddc2.Notification = DDC2.ServerInfo.ServerID+ ` has recieved a `+(Func.Capitalize(msg.ddc2.requesttype))+ ` -Update command request from `+msg.author.username+ `. Please wait while this task is processed....`
    msg.payload = '-'+msg.ddc2.requesttype+" "+doStopUpdate
    return msg
}
////////////////////////////////////////////////////////////////////////////////////////////////////
var StopDCS = msglow.indexOf('-dcs')
var doStopDCS = ""
if(StopDCS != -1) {
    doStopDCS = "-StopDCS"
    msg.ddc2.Stop.DCS = true
    msg.ddc2.Notification = DDC2.ServerInfo.ServerID+ ` has recieved a `+(Func.Capitalize(msg.ddc2.requesttype))+ ` -DCS command request from `+msg.author.username+ `. Please wait while this task is processed....`
    msg.payload = '-'+msg.ddc2.requesttype+" "+doStopDCS
    return msg
}
////////////////////////////////////////////////////////////////////////////////////////////////////
var StopSRS = msglow.indexOf('-srs')
var doStopSRS = ""
if(StopSRS != -1) {
    doStopSRS = "-StopSRS"
    msg.ddc2.Stop.SRS = true
    msg.ddc2.Notification = DDC2.ServerInfo.ServerID+ ` has recieved a `+(Func.Capitalize(msg.ddc2.requesttype))+ ` -SRS command request from `+msg.author.username+ `. Please wait while this task is processed....`
    msg.payload = '-'+msg.ddc2.requesttype+" "+doStopSRS
    return msg
}
////////////////////////////////////////////////////////////////////////////////////////////////////
//Execution of Default Stop Message
msg.ddc2.Notification = DefaultNote
msg.ddc2.Stop.Game = true
msg.payload = DefaultPayload
return msg

