var DDC2 = flow.get('DDC2');
msg.channel = DDC2.Channel.ServerStatus
return msg

var Func = flow.get('Func');
var DDC2 = flow.get('DDC2');
var DCS = flow.get('DCS');
if((msg.payload.dataType == "STATUS") || (msg.payload.dataType == "INIT")) {
    flow.set('DCS.missionRT', Func.FormatTime(msg.payload.dataPayload.missionRT))
    flow.set('DCS.missionFRT', Func.FormatTime(msg.payload.dataPayload.missionRT))
    flow.set('DCS.serverFPS', msg.payload.dataPayload.serverFPS)
    flow.set('DCS.missionRealTime', Func.TimeOfDay(msg.payload.dataPayload.mission.missionStartTime + msg.payload.dataPayload.missionRT))
    flow.set('DCS.playersCount', msg.payload.dataPayload.playersCount)
    flow.set('DCS.status', msg.payload.dataPayload.status)
    flow.set('DCS.config', msg.payload.dataPayload.config)
    flow.set('DCS.playerDetails', msg.payload.dataPayload.playerDetails)
    flow.set('DCS.currentMission', msg.payload.dataPayload.mission)
    flow.set('DCS.WeatherName', Func.GetWeatherName(DCS.currentMission.missionWeather.clouds.preset))
    }
if(msg.payload.dataType == "PILOT_TABLE") {
    flow.set('DCS.playerDetails', msg.payload.dataPayload.playerDetails)
    flow.set('DCS.playersCount', msg.payload.dataPayload.playersCount)
}
if(msg.payload.dataType == "HEARTBEAT") {
    flow.set('DCS.missionRT',msg.payload.dataPayload.missionRT)
    flow.set('DCS.missionFRT', Func.FormatTime(msg.payload.dataPayload.missionRT))
    flow.set('DCS.serverFPS', msg.payload.dataPayload.serverFPS)
    if(typeof DCS.currentMission === 'undefined') {
        flow.set('DCS.missionRealTime', 'UNKNOWN')
        flow.set('DCS.WeatherName', 'UNKNOWN')
    } else {
        flow.set('DCS.missionRealTime', Func.TimeOfDay(DCS.currentMission.missionStartTime + msg.payload.dataPayload.missionRT))
        flow.set('DCS.WeatherName', Func.GetWeatherName(DCS.currentMission.missionWeather.clouds.preset))
        }    
    }
if(DDC2.Status.DCS.Offline) {
    msg.ddc2 = {
        'requesttype' : 'status',
        'silent' : true
        }
    msg.data = {
        'createdTimestamp' : new Date()
        }
    msg.payload = '-status'
    return [msg,null]
} else {
    msg.action = 'edit'
    msg.channel = {
        'id' : DDC2.Channel.ServerStatus
        }
    msg.message = {
        'id' : DDC2.Channel.ServerStatusMessage
        }


    var DCS_StartTime = new Date(DDC2.Status.DCS.StartTime.Year,(DDC2.Status.DCS.StartTime.Month - 1),DDC2.Status.DCS.StartTime.Day,DDC2.Status.DCS.StartTime.Hour,DDC2.Status.DCS.StartTime.Minute,DDC2.Status.DCS.StartTime.Second);
    var DCS_Uptime = Func.TimeDelta((DCS_StartTime), new Date(),true, false);
    msg.DCSUpTime = DCS_Uptime
    if (DDC2.AppConfig.SRS.Installed == true) {
        var SRS_StartTime = new Date(DDC2.Status.SRS.StartTime.Year, (DDC2.Status.SRS.StartTime.Month - 1), DDC2.Status.SRS.StartTime.Day, DDC2.Status.SRS.StartTime.Hour, DDC2.Status.SRS.StartTime.Minute, DDC2.Status.SRS.StartTime.Second);
        var SRS_Uptime = Func.TimeDelta((SRS_StartTime), new Date(), true, false);
        msg.SRSUpTime = SRS_Uptime
    }
    //var FormattedTimeStamp = Func.FormatDateTime(msg.payload.timeStamp, 'en')
    //msg.FormattedTimeStamp = FormattedTimeStamp
    msg.FormattedTimeStamp = Func.FormatDateTime(msg.payload.timeStamp, 'en')
    node.status({ fill: "green", shape: "ring", text: "As at " +  msg.FormattedTimeStamp});
    return [null,msg]
}

// @ts-nocheck
var Func = flow.get('Func');
var DDC2 = flow.get('DDC2');
var DCS = flow.get('DCS');
//////////////////////////////////////////////////////////////////////////////////////////////////
if(DDC2.Notifications.enabled) {
    //NOTIFICATIONS
    msg.channel = DDC2.Channel.ServerNotifications
    msg.notifications = {
        'friendly_fire' : false,
          'mission_end' : false,
     'mission_load_end' : false,
                 'kill' : false,
            'self_kill' : false,
          'change_slot' : false, 
              'connect' : false,
           'disconnect' : false,
                'crash' : false,
                'eject' : false,
              'takeoff' : false,
              'landing' : false,
          'pilot_death' : false
    }

//////////////////////////////////////////////////////////////////////////////////////////////////
    if(DDC2.Notifications.friendly_fire & (msg.payload.dataPayload.eventType == "friendly_fire")) {
        msg.notifications.friendly_fire = true
        node.status({fill:"green",shape:"dot",text:"friendly_fire"});
        return msg
    }

    if (DDC2.Notifications.mission_load_end & (msg.payload.dataPayload.eventType == "mission_load_end")) {
        msg.notifications.mission_load_end = true
        node.status({ fill: "green", shape: "dot", text: "mission_load_end" });
        return msg
    }

    if(DDC2.Notifications.mission_end & (msg.payload.dataPayload.eventType == "mission_end")) {
        msg.notifications.mission_end = true
        node.status({fill:"green",shape:"dot",text:"mission_end"});
        return msg
    }

    if(DDC2.Notifications.kill & (msg.payload.dataPayload.eventType == "kill")) {
        msg.notifications.kill = true
        if (msg.payload.dataPayload.killerInfo.name == "") {
            if (msg.payload.dataPayload.killerSide == 0) {msg.payload.dataPayload.killerInfo.name = "Neutral Forces"}
            if (msg.payload.dataPayload.killerSide == 1) {msg.payload.dataPayload.killerInfo.name = "Red Forces"}
            if (msg.payload.dataPayload.killerSide == 2) {msg.payload.dataPayload.killerInfo.name = "Blue Forces"}
        }
        msg.payload.dataPayload.killerUnitType = Func.GetAircraftName(msg.payload.dataPayload.killerUnitType)
        if (msg.payload.dataPayload.victimUnitType == "") {
            msg.payload.dataPayload.victimUnitType = "Target"
        } else {
            msg.payload.dataPayload.victimUnitType = Func.GetAircraftName(msg.payload.dataPayload.victimUnitType)
        }
        if (msg.payload.dataPayload.weaponName == "") {
            if (DDC2.Notifications.RandomWeapon) {
                msg.payload.dataPayload.weaponName = Func.MagicalWeaponNameGenerator()
            } else {
                msg.payload.dataPayload.weaponName = 'weapon of some kind'
            }
        }
        node.status({fill:"green",shape:"dot",text:"kill"});
        return msg
    }

    if(DDC2.Notifications.self_kill & (msg.payload.dataPayload.eventType == "self_kill")) {
        msg.notifications.self_kill = true
        node.status({fill:"green",shape:"dot",text:"self_kill"});
        return msg
    }

    if(DDC2.Notifications.change_slot & (msg.payload.dataPayload.eventType == "change_slot")) {
        if(typeof msg.payload.dataPayload.playerInfo != "undefined") {
            if(msg.payload.dataPayload.playerInfo.side != msg.payload.dataPayload.prevSide) {
                msg.payload.dataPayload.playerInfo.sideSTR = Func.GetCoalition(msg.payload.dataPayload.playerInfo.side)
                msg.notifications.change_slot = true
                node.status({fill:"green",shape:"dot",text:"change_slot"});            
                return msg
            }
        }
    }

    if(DDC2.Notifications.connect & (msg.payload.dataPayload.eventType == "connect")) {
        msg.notifications.connect = true
        node.status({fill:"green",shape:"dot",text:"connect"});
        return msg
    }

    if(DDC2.Notifications.disconnect & (msg.payload.dataPayload.eventType == "disconnect")) {
        msg.notifications.disconnect = true
        node.status({fill:"green",shape:"dot",text:"disconnect"});
        return msg
    }

    if(DDC2.Notifications.crash & (msg.payload.dataPayload.eventType == "crash")) {
        msg.notifications.crash = true
        msg.payload.dataPayload.unitType = Func.GetAircraftName(msg.payload.dataPayload.unitType)
        node.status({fill:"green",shape:"dot",text:"crash"});
        return msg
    }

    if(DDC2.Notifications.eject & (msg.payload.dataPayload.eventType == "eject")) {
        msg.notifications.eject = true
        msg.payload.dataPayload.unitType = Func.GetAircraftName(msg.payload.dataPayload.unitType)
        node.status({fill:"green",shape:"dot",text:"eject"});
        return msg
    }

    if(DDC2.Notifications.takeoff & (msg.payload.dataPayload.eventType == "takeoff")) {
        msg.notifications.takeoff = true
        msg.payload.dataPayload.unitType = Func.GetAircraftName(msg.payload.dataPayload.unitType)
        node.status({fill:"green",shape:"dot",text:"takeoff"});
        return msg
    }


    if(DDC2.Notifications.landing & (msg.payload.dataPayload.eventType == "landing")) {
        msg.notifications.landing = true
        msg.payload.dataPayload.unitType = Func.GetAircraftName(msg.payload.dataPayload.unitType)
        node.status({fill:"green",shape:"dot",text:"landing"});
        return msg
    }

    if(DDC2.Notifications.pilot_death & (msg.payload.dataPayload.eventType == "pilot_death")) {
        msg.notifications.pilot_death = true
        if(DDC2.Notifications.RandomDeath) {
            msg.payload.deathnotice = Func.DeathNotice()
            msg.payload.wrecknotice = Func.WreckNotice()
        } else {
            msg.payload.deathnotice = 'is dead'
            msg.payload.wrecknotice = 'is a smoldering wreck. Rest in peace.'
        }
        msg.payload.dataPayload.unitType = Func.GetAircraftName(msg.payload.dataPayload.unitType)
        node.status({fill:"green",shape:"dot",text:"pilot_death"});
        return msg
    }
} else {
    node.status({fill:"red",shape:"dot",text:"Notifications not Enabled"});
}

