var MsgSent = context.get('MsgSent') || 1;
var Func = flow.get('Func'); //Loads Flow Functions into memory
var DDC2 = flow.get('DDC2'); //Loads DDC2 Configuration Data and sets it as a Variable DDC2

//Check if message first character 
if (msg.channel.type != "dm") {
    if (msg.payload.substr(0,1) == DDC2.ServerInfo.CommandPrefix) {
        var msglow = msg.payload.toLowerCase();
        var splitmsg = msglow.split(" ",3);
        var command = splitmsg[0];
        var server = splitmsg[1];
        var requesttype = command.replace(DDC2.ServerInfo.CommandPrefix,'')
        var Permissions = flow.get('DDC2.Permissions.'+requesttype)
        var tmstmp = msg.data.createdTimestamp;
        var localdatetime = (new Date(tmstmp)).toLocaleString();
        var access = "NOTSET";
        msg.ddc2 = {
                  "request" : command,
            "FormattedTime" : localdatetime,
           "RequestChannel" : msg.channel.name,
         "RequestChannelID" : msg.channel.id,
              "requesttype" : requesttype,
                   "access" : access,
               "authorized" : false,
                    "valid" : false,
                    'silent' : false,
                  "comment" : "",
             "Notification" : "",
                      "TTC" : "", //Time To Completion, This measures the time the system recieved the request through to the time the request was completed. This item is used in further down in the flow
                      "raw" : msg.payload
        }
        if (requesttype == "test") { //All Servers should respond to a test command
            msg.ddc2.valid = true;
            if (Func.GrantAccess(Permissions,msg.member.roles)) {
                msg.ddc2.authorized = true;
                msg.ddc2.access = "GRANTED"
                msg.ddc2.Notification = DDC2.ServerInfo.ServerID+" successfully recieved a "+(Func.Capitalize(msg.ddc2.requesttype))+" message request from "+msg.author.username+ " in the "+msg.ddc2.RequestChannel+" channel on the " +msg.ddc2.FormattedTime;
                } 
            return msg;
            }
        if (DDC2.ServerInfo.ENABLE_HELP) {
            if (requesttype == "help") {
                if (Func.GrantAccess(Permissions,msg.member.roles)) {
                msg.ddc2.authorized = true;
                msg.ddc2.access = "GRANTED"
                msg.ddc2.Notification = ""
                return msg;        
                }
            } 
        }
        if (requesttype == "acclink") {
            if ((server != null && server == DDC2.ServerInfo.ServerID) || (DDC2.ServerInfo.LINK_MASTER && server == null)) {
                if (Func.GrantAccess(Permissions,msg.member.roles)) {
                    msg.ddc2.authorized = true;
                    msg.ddc2.access = "GRANTED"
                    msg.ddc2.Notification = ""
                    return msg;
                }
            }
        } 
        if(server == DDC2.ServerInfo.ServerID.toLowerCase()) {return msg} //use this line if you have multiple servers operating from a single channel
        //return msg //use this line if you have one server and you do not wish to have to define the server name to interact with you server.
    }
}

