var DDC2 = flow.get('DDC2');
var DBTABLES = flow.get("DBTABLES") || false;
var SEND = false
var dataPayload = {}
var notify = ""
var banList = ""
var softBanList = ""

if (DBTABLES === false) {
    //Initialize BAN_LIST
    flow.set("DBTABLES", {
        banList: msg.payload.banList,
        softBanList: msg.payload.softBanList
    })
    banList = { banList: msg.payload.banList }
    Object.assign(dataPayload, banList)
    softBanList = { softBanList: msg.payload.softBanList }
    Object.assign(dataPayload, softBanList)
    notify = "DBTABLES INIT"
    SEND = true
} else {
    if (!(JSON.stringify(msg.payload.banList) === JSON.stringify(DBTABLES.banList))) {
        flow.set("DBTABLES.banList", msg.payload.banList)
        banList = { banList: msg.payload.banList }
        Object.assign(dataPayload, banList)
        SEND = true
        notify = "DBTABLES.banList SEND"
    }
    if (!(JSON.stringify(msg.payload.softBanList) === JSON.stringify(DBTABLES.softBanList))) {
        flow.set("DBTABLES.softBanList", msg.payload.softBanList)
        softBanList = {softBanList: msg.payload.softBanList}
        Object.assign(dataPayload, softBanList)
        SEND = true
        notify = "DBTABLES.softbanList SEND"
    }
}

if (SEND) {
    var TS = new Date()
    //dataPayload.banList = msg.payload
    msg.host = '127.0.0.1'
    msg.port = DDC2.ServerInfo.DCS_Port
    var StructuredData = {
        'initiator': "DDC2",
        'serverID': DDC2.ServerInfo.ServerID,
        'dataType': 'DBTABLES',
        'dataID': msg._msgid,
        'dataPayload': dataPayload,
        //'logTable': true,
        'timeStamp': new Date(TS).getTime()
    }
    msg.payload = ''
    msg.payload = StructuredData
    node.status({ fill: "green", shape: "ring", text: notify });
    return msg;
} else {
    node.status({ fill: "yellow", shape: "ring", text: "NOTHING SENT" });
}

