// This bit of code requests data from powershell to populate 
// the configuration items that were previously in Node-Red.
var payload = "-init";  
msg.payload = payload;
var command = "!init";
var requesttype = command.replace('!','')
var access = "GRANTED";
msg.data = {
    "createdTimestamp": Date.now()
    }
var tmstmp = msg.data.createdTimestamp;
var localdatetime = (new Date(tmstmp)).toLocaleString();

msg.message = {
          "request" : command,
    "FormattedTime" : localdatetime,
   "RequestChannel" : "",
 "RequestChannelID" : "",
      "requesttype" : requesttype,
           "access" : access,
       "authorized" : true,
            "valid" : true,
          "comment" : "",
     "Notification" : "",
              "TTC" : "", //Time To Completion, This measures the time the system recieved the request through to the time the request was completed. This item is used in further down in the flow
              "raw" : msg.payload
}
node.status({fill:"green",shape:"dot",text:"DDC2 Initialized"});
return msg;

var payload = "-AutoStart"; //Changed while testing (Switch between -status or -doupdate)
msg.payload = payload;
var DDC2 = flow.get('DDC2');
msg.author = {
        "username": DDC2.ServerInfo.ServerID,
        "id": DDC2.ServerInfo.ServerID
    }

msg.data = {
    "createdTimestamp": Date.now()
    }
msg.channel = {
    "id": DDC2.Channel.Admin
    }

var tmstmp = msg.data.createdTimestamp;
var localdatetime = (new Date(tmstmp)).toLocaleString();
var command = "!autostart";
var requesttype = command.replace('!','')
var access = "GRANTED";

msg.message = {
          "request" : command,
    "FormattedTime" : localdatetime,
   "RequestChannel" : "NOT APPLICABLE",
 "RequestChannelID" : "NOT APPLICABLE",
      "requesttype" : requesttype,
           "access" : access,
       "authorized" : true,
            "valid" : true,
          "comment" : "Server AutoStart",
     "Notification" : DDC2.ServerInfo.ServerID+ ` has initialized its ` +requesttype+ ` sequence on the ` +localdatetime,
              "raw" : msg.payload
}

return msg

//DO NOT MODIFY THIS DATA!!
//This sets the entire DDC2 variable structure from the ddc2_config.ps1, dont remove it!
flow.set('DDC2', 0);
flow.set('DDC2', msg.PS);
var TimeDate = new Date(msg.data.createdTimestamp).toLocaleString();
node.status({fill:"green",shape:"dot",text:TimeDate});

return msg;

flow.set('DDC2.Ready', true);
node.status({fill:"green",shape:"dot",text:"SERVER READY!"});

//This line checks if the refresh command has been called, if so it does not execute the Autostart sequence.
if(msg.message.requesttype == "refresh") {} else {return msg}

var DDC2 = flow.get('DDC2');

//var perms = DDC2.Permissions;
