return msg;
}

/*
Checks the Discord Message User groups against the specific command being run and either passes the message onto Pre-Processing or sends an 
access denied message and logs the access denied request. Note, Access Granted is assumed from this point so the Pre-Processing section needs 
to take care of the task notification after the command in questions syntax is checked.
*/


////////////////////////////////////////////////////////////////////////////////////////////////////
////Initialize Variables
var MsgSent = context.get('MsgSent') || 1;

//Get Flow Variables
var Func = flow.get('Func');
var DDC2 = flow.get('DDC2');
var CommandPermissions = flow.get('DDC2.Permissions.'+msg.message.requesttype)

if (Func.GrantAccess(CommandPermissions,msg.member._roles)) {
    msg.message.authorized = true;
    msg.message.access = "GRANTED"
    }
else {
    msg.message.authorized = false;
    msg.message.Notification = `!!ACCESS DENIED!! ` +msg.author.username+ ` you do not have access to the `+(Func.Capitalize(msg.message.requesttype))+` command on server ` +DDC2.ServerInfo.ServerID+ `, message ignored on the `+msg.message.FormattedTime
    msg.message.access = "DENIED"
    node.status({fill:"red",shape:"dot",text: "DENIED:::"+MsgSent});
    context.set('MsgSent', (MsgSent + 1));
    }

if(msg.message.authorized) {return [null,msg]} else {return [msg,null]}

////Initialize Variables
var Func = flow.get('Func');
var DDC2 = flow.get('DDC2');
////////////////////////////////////////////////////////////////////////////////////////////////////
var ttc = Func.DaysBetween((new Date(msg.data.createdTimestamp)), new Date());
msg.message.TTC = ttc
msg.message.comment = "Request Actioned"
var extraP = 'p'
if(msg.message.requesttype != 'stop') {extraP = ''}

msg.PS.Server.UpTimeStr = Func.pad(msg.PS.Server.UpTime.Days,2)+ `:`+Func.pad(msg.PS.Server.UpTime.Hours,2)+`:`+Func.pad(msg.PS.Server.UpTime.Minutes,2)+`:`+Func.pad(msg.PS.Server.UpTime.Seconds,2)

if(msg.PS.DCS.IsActive === true) {
    msg.PS.DCS.RunTimeStr = Func.pad(msg.PS.DCS.RunTime.Days,2)+ `:`+Func.pad(msg.PS.DCS.RunTime.Hours,2)+`:`+Func.pad(msg.PS.DCS.RunTime.Minutes,2)+`:`+Func.pad(msg.PS.DCS.RunTime.Seconds,2)
    msg.PS.DCS.PriorityStr = Func.GetPriority(msg.PS.DCS.PriorityClass)
}
if(msg.PS.SRS.IsActive === true) {
    msg.PS.SRS.RunTimeStr = Func.pad(msg.PS.SRS.RunTime.Days,2)+ `:`+Func.pad(msg.PS.SRS.RunTime.Hours,2)+`:`+Func.pad(msg.PS.SRS.RunTime.Minutes,2)+`:`+Func.pad(msg.PS.SRS.RunTime.Seconds,2)
    msg.PS.SRS.PriorityStr = Func.GetPriority(msg.PS.SRS.PriorityClass)
}
msg.message.Notification = DDC2.ServerInfo.ServerID+ ` has `+(Func.Capitalize(msg.message.requesttype))+extraP+`ed the server. The task took `+msg.message.TTC+` to complete.`
////////////////////////////////////////////////////////////////////////////////////////////////////
return msg

////Initialize Variables
var Func = flow.get('Func');
var DDC2 = flow.get('DDC2');
////////////////////////////////////////////////////////////////////////////////////////////////////
var ttc = Func.DaysBetween((new Date(msg.data.createdTimestamp)), new Date());
msg.message.TTC = ttc
msg.message.comment = "Request Actioned"
msg.message.Notification = DDC2.ServerInfo.ServerID+ ` is wondering how you are seeing this message if the server just restarted before it had a chance to send this message. Will wonders never cease! The task took `+msg.message.TTC+` to complete.`
////////////////////////////////////////////////////////////////////////////////////////////////////
return msg

var MsgSent = context.get('MsgSent') || 1;
var DDC2 = flow.get('DDC2');
var Func = flow.get('Func');
msg.message.Notification = "" //Stops the Notification system from sending out a message.
msg.message.comment = "Cached Response"
msg.message.info = {
    "ShowBluPWD" : DDC2.ServerInfo.ShowBluPWD,
    "ShowRedPWD" : DDC2.ServerInfo.ShowRedPWD,
    "ShowSrvPWD" : DDC2.ServerInfo.ShowSrvPWD,
    "ShowLotATC" : DDC2.ServerInfo.ShowLotATC,
    "ShowTACView" : DDC2.ServerInfo.ShowTACView
    }

node.status({fill:"green",shape:"ring",text: "IP's misplaced: "+MsgSent});
if(msg.channel.id == DDC2.Channel.Admin) {
//Show everything

//DDC2.AppConfig.DCS.password = "chickenFucker"
msg.message.info.ShowBluPWD = true
msg.message.info.ShowRedPWD = true
msg.message.info.ShowSrvPWD = true
msg.message.info.ShowLotATC = true
msg.message.info.ShowTACView = true
context.set('MsgSent', (MsgSent + 1));
return msg;
}

if(msg.channel.id == DDC2.Channel.Red) {
//Set Password to RED
msg.message.info.ShowBluPWD = false
context.set('MsgSent', (MsgSent + 1));
return msg;
}

if(msg.channel.id == DDC2.Channel.Blue) {
//Set Password to RED
msg.message.info.ShowRedPWD = false
context.set('MsgSent', (MsgSent + 1));
return msg;
}

if(msg.channel.id == DDC2.Channel.Support) {
msg.message.info.ShowBluPWD = false
msg.message.info.ShowRedPWD = false
context.set('MsgSent', (MsgSent + 1));
return msg;
}

var HelpEnabled = flow.get('DDC2.ServerInfo.ENABLE_HELP');
if (HelpEnabled) {
    node.status({fill:"green",shape:"dot",text:"Help is enabled"});
    }
else {
    node.status({fill:"green",shape:"ring",text:"Help is disabled"});
    }

