var DDC2 = flow.get('DDC2');
msg.channel.id = DDC2.Channel.Log;
return msg;

Func = {
        GrantAccess : function (haystack, arr) {
                    return arr.some(function (v) {
                    return haystack.indexOf(v) >= 0;
                    })},
                pad : function (num, size) {
                    var s = num+"";
                    while (s.length < size) s = "0" + s;
                    return s;
                    },
        DaysBetween : function (date1, date2) {
                    var one_day=1000*60*60*24;
                    var date1_ms = date1.getTime();
                    var date2_ms = date2.getTime();
                    var difference_ms = date2_ms - date1_ms;
                    difference_ms = Math.round(difference_ms/1000,3);
                    var mseconds = difference_ms % 1000;
                    var seconds = Math.floor(difference_ms % 60);
                    difference_ms = difference_ms/60; 
                    var minutes = Math.floor(difference_ms % 60);
                    difference_ms = difference_ms/60; 
                    var hours = Math.floor(difference_ms % 24);  
                    var days = Math.floor(difference_ms/24);
                    return Func.pad(minutes,2) + ':' + Func.pad(seconds,2) + '.' + Func.pad(mseconds,3);
                    },
        GetPriority : function (priority) {
                    var PriorityTable = { 64:"Low", 
                    16384:"Below Normal", 
                    32:"Normal", 
                    32768:"Above Normal", 
                    128:"High",
                    256:"Realtime"
        			};
                    return (PriorityTable[priority])
                    },
         Capitalize : function(str) {
                    const capitalized = str.charAt(0).toUpperCase() + str.slice(1);
                    return capitalized;
                    },
       GetCoalition : function (coal) {
                    var CoalitionTable = { 0:"Neutral / Spectator", 
                    1:"Red Forces", 
                    2:"Blue Forces"
        			};
                    return (CoalitionTable[coal])
                    },
  ValidateIPaddress : function (IPAddr) {
                    return /^(([0-9]|[1-9][0-9]|1[0-9]{2}|2[0-4][0-9]|25[0-5])\.){3}([0-9]|[1-9][0-9]|1[0-9]{2}|2[0-4][0-9]|25[0-5])$|^(([a-zA-Z]|[a-zA-Z][a-zA-Z0-9\-]*[a-zA-Z0-9])\.)*([A-Za-z]|[A-Za-z][A-Za-z0-9\-]*[A-Za-z0-9])$|^\s*((([0-9A-Fa-f]{1,4}:){7}([0-9A-Fa-f]{1,4}|:))|(([0-9A-Fa-f]{1,4}:){6}(:[0-9A-Fa-f]{1,4}|((25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)(\.(25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)){3})|:))|(([0-9A-Fa-f]{1,4}:){5}(((:[0-9A-Fa-f]{1,4}){1,2})|:((25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)(\.(25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)){3})|:))|(([0-9A-Fa-f]{1,4}:){4}(((:[0-9A-Fa-f]{1,4}){1,3})|((:[0-9A-Fa-f]{1,4})?:((25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)(\.(25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)){3}))|:))|(([0-9A-Fa-f]{1,4}:){3}(((:[0-9A-Fa-f]{1,4}){1,4})|((:[0-9A-Fa-f]{1,4}){0,2}:((25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)(\.(25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)){3}))|:))|(([0-9A-Fa-f]{1,4}:){2}(((:[0-9A-Fa-f]{1,4}){1,5})|((:[0-9A-Fa-f]{1,4}){0,3}:((25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)(\.(25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)){3}))|:))|(([0-9A-Fa-f]{1,4}:){1}(((:[0-9A-Fa-f]{1,4}){1,6})|((:[0-9A-Fa-f]{1,4}){0,4}:((25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)(\.(25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)){3}))|:))|(:(((:[0-9A-Fa-f]{1,4}){1,7})|((:[0-9A-Fa-f]{1,4}){0,5}:((25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)(\.(25[0-5]|2[0-4]\d|1\d\d|[1-9]?\d)){3}))|:)))(%.+)?\s*$/.test(IPAddr);
                    }
    }
flow.set('Func', Func);
node.status({fill:"green",shape:"dot",text:"Functions Loaded"});

