////////////////////////////////////////////////////////////////////////////////////////////////////
msg.message.valid = true;
msg.message.Notification = DDC2.ServerInfo.ServerID+ ` has received your request `+msg.author.username+ ` and has begun processing your `+(Func.Capitalize(msg.message.requesttype))+` request, please wait....`
msg.payload = '-'+msg.message.requesttype
////////////////////////////////////////////////////////////////////////////////////////////////////
var MsgSent = context.get('MsgSent') || 1;
node.status({fill:"green",shape:"ring",text: "Settings tweaked: "+MsgSent});
context.set('MsgSent', (MsgSent + 1));
