////Initialize Variables
var Func = flow.get('Func');
var DDC2 = flow.get('DDC2');
////////////////////////////////////////////////////////////////////////////////////////////////////
var ttc = Func.DaysBetween((new Date(msg.data.createdTimestamp)), new Date());
msg.message.TTC = ttc
msg.message.comment = "Request Actioned"
msg.message.Notification = DDC2.ServerInfo.ServerID+ ` has updated its settings from the DDC2 configuration files. The task took `+msg.message.TTC+` to complete.`
////////////////////////////////////////////////////////////////////////////////////////////////////
return msg

var MsgSent = context.get('MsgSent') || 1;
var Func = flow.get('Func'); //Needed for Notification
var DDC2 = flow.get('DDC2'); //Needed for Notification
msg.message.comments = "Request Actioned"
msg.message.Notification = DDC2.ServerInfo.ServerID+" successfully recieved a "+(Func.Capitalize(msg.message.requesttype))+" message request from "+msg.author.username+ " in the "+msg.message.RequestChannel+" channel on the " +msg.message.FormattedTime;
msg.message.comment = "Cached Response"
return msg;

////Initialize Variables
var Func = flow.get('Func');
var DDC2 = flow.get('DDC2');

flow.set('DDC2.Status', msg.PS);
////////////////////////////////////////////////////////////////////////////////////////////////////
var ttc = Func.DaysBetween((new Date(msg.data.createdTimestamp)), new Date());
msg.message.TTC = ttc
msg.message.comment = "Request Actioned"

msg.PS.Server.UpTimeStr = Func.pad(msg.PS.Server.UpTime.Days,2)+ `:`+Func.pad(msg.PS.Server.UpTime.Hours,2)+`:`+Func.pad(msg.PS.Server.UpTime.Minutes,2)+`:`+Func.pad(msg.PS.Server.UpTime.Seconds,2)

if(msg.PS.DCS.IsActive === true) {
    msg.PS.DCS.RunTimeStr = Func.pad(msg.PS.DCS.RunTime.Days,2)+ `:`+Func.pad(msg.PS.DCS.RunTime.Hours,2)+`:`+Func.pad(msg.PS.DCS.RunTime.Minutes,2)+`:`+Func.pad(msg.PS.DCS.RunTime.Seconds,2)
    msg.PS.DCS.PriorityStr = Func.GetPriority(msg.PS.DCS.PriorityClass)
}
if(msg.PS.SRS.IsActive === true) {
    msg.PS.SRS.RunTimeStr = Func.pad(msg.PS.SRS.RunTime.Days,2)+ `:`+Func.pad(msg.PS.SRS.RunTime.Hours,2)+`:`+Func.pad(msg.PS.SRS.RunTime.Minutes,2)+`:`+Func.pad(msg.PS.SRS.RunTime.Seconds,2)
    msg.PS.SRS.PriorityStr = Func.GetPriority(msg.PS.SRS.PriorityClass)
}


if(msg.PS.Updating) {

if (typeof msg.message.Update === 'undefined') {
    //First Run
    msg.delay = (DDC2.ServerInfo.UpdateLoop * 1000)
    msg.message.Update = {
            "Status" : "Initializing",
          "FirstRun" : true,
           "LOT_Upd" : "Update not started",
           "SRS_Upd" : "Update not started",
           "DCS_Upd" : "Update not started",
          "Finished" : false
    }
} else {
    if(msg.PS.Updating) {
        msg.message.Update.Status = "Updating"
        msg.message.Update.FirstRun = false
        msg.message.Update.Finished = false
    } else {
        msg.message.Update.Status = "Done"
        msg.message.Update.FirstRun = false
        msg.message.Update.Finished = true
    }
}
if(msg.PS.DCS.IsActive) {msg.message.Update.Finished = true}
if(msg.PS.SRS.IsActive) {msg.message.Update.Finished = true}

if(msg.PS.LOT_Updater.IsActive) {
    msg.message.Update.LOT_Upd = "Updating to current "+DDC2.ServerInfo.LotBuild+" Build"
}
if(msg.PS.SRS_Updater.IsActive) {
    msg.message.Update.LOT_Upd = "Update Complete ("+DDC2.ServerInfo.LotBuild+")"
    msg.message.Update.SRS_Upd = "Updating to current "+DDC2.ServerInfo.SRSBuild+" Build"
}
if(msg.PS.DCS_Updater.IsActive) {
    msg.message.Update.LOT_Upd = "Update Complete ("+DDC2.ServerInfo.LotBuild+")"
    msg.message.Update.SRS_Upd = "Update Complete ("+DDC2.ServerInfo.SRSBuild+")"
    msg.message.Update.DCS_Upd = "Updating to current "+DDC2.ServerInfo.DCSBuild+" Build"
}
if(msg.message.Update.DCS_Upd == "Updating") {
    if (msg.PS.DCS_Updater.Offline) {
        msg.message.Update.DCS_Upd = "Update Complete ("+DDC2.ServerInfo.DCSBuild+")"
    }
}
if(msg.PS.DCS.Version != "") {
    if(DDC2.AppConfig.DCS.Version != msg.PS.DCS.Version) {
        flow.set('DDC2.AppConfig.DCS.Version', msg.PS.DCS.Lot_Version)
    }
}
if(msg.PS.DCS.Lot_Version != "") {
    if(DDC2.AppConfig.LotATC.Version != msg.PS.DCS.Lot_Version) {
        flow.set('DDC2.AppConfig.LotATC.Version', msg.PS.DCS.Lot_Version)
    }
}
if(DDC2.AppConfig.SRS.Version != msg.PS.SRS.Version) {
    flow.set('DDC2.AppConfig.SRS.Version', msg.PS.SRS.Version)
}

}

msg.message.Notification = ""
return msg

