func:
/*
Stop Command
usage

!stop <serverID> //DEFAULT ACTION: Stops DCS and SRS processes (Same as -Game)

!stop <serverID> -All  //Stops All DCS and Update processes

!stop <serverID> -Game //Stops DCS and SRS processes

!stop <serverID> -DCS //Stops DCS process

!stop <serverID> -SRS //Stops SRS process

!stop <serverID> -Update //Stops Update process

Options for Default are 

-StopAll
-StopGame
-StopDCS
-StopSRS
-StopUpdate

*/
////Initialize Variables
var Func = flow.get('Func');
var DDC2 = flow.get('DDC2');
////////////////////////////////////////////////////////////////////////////////////////////////////
var MsgSent = context.get('MsgSent') || 1;
node.status({fill:"green",shape:"ring",text: "Airbrakes Deployed : "+MsgSent});
context.set('MsgSent', (MsgSent + 1));
////////////////////////////////////////////////////////////////////////////////////////////////////
msg.message.Stop = {
         "Game" : false,
          "All" : false,
          "DCS" : false,
          "SRS" : false,
       "Update" : false
    }
////////////////////////////////////////////////////////////////////////////////////////////////////
msg.message.valid = true;
var msglow = msg.message.raw.toLowerCase();
////////////////////////////////////////////////////////////////////////////////////////////////////
//DEFAULT ACTION (will be executed is no other switches are detected during message analysis)
var DefaultNote = msg.message.Notification = DDC2.ServerInfo.ServerID+ ` has recieved a `+(Func.Capitalize(msg.message.requesttype))+ ` command request from `+msg.author.username+ `. Please wait while this task is processed....`
var DefaultPayload = '-'+msg.message.requesttype+" -StopGame" //DEFAULT SETTING IS -StopGame
////////////////////////////////////////////////////////////////////////////////////////////////////

var StopAll = msglow.indexOf('-all')
var doStopAll = ""
if(StopAll != -1) {
    doStopAll = "-StopAll"
    msg.message.Stop.All = true
    msg.message.Notification = DDC2.ServerInfo.ServerID+ ` has recieved a `+(Func.Capitalize(msg.message.requesttype))+ ` -all command request from `+msg.author.username+ `. Please wait while this task is processed....`
    msg.payload = '-'+msg.message.requesttype+" "+doStopAll
    return msg
}
////////////////////////////////////////////////////////////////////////////////////////////////////
var StopGame = msglow.indexOf('-game')
var doStopGame = ""
if(StopGame != -1) {
    doStopGame = "-StopGame"
    msg.message.Stop.Game = true
    msg.message.Notification = DDC2.ServerInfo.ServerID+ ` has recieved a `+(Func.Capitalize(msg.message.requesttype))+ ` -Game command request from `+msg.author.username+ `. Please wait while this task is processed....`
    msg.payload = '-'+msg.message.requesttype+" "+doStopGame
    return msg
}
////////////////////////////////////////////////////////////////////////////////////////////////////
var StopUpdate = msglow.indexOf('-update')
var doStopUpdate = ""
if(StopUpdate != -1) {
    doStopUpdate = "-StopUpdate"
    msg.message.Stop.Update = true
    msg.message.Notification = DDC2.ServerInfo.ServerID+ ` has recieved a `+(Func.Capitalize(msg.message.requesttype))+ ` -Update command request from `+msg.author.username+ `. Please wait while this task is processed....`
    msg.payload = '-'+msg.message.requesttype+" "+doStopUpdate
    return msg
}
////////////////////////////////////////////////////////////////////////////////////////////////////
var StopDCS = msglow.indexOf('-dcs')
var doStopDCS = ""
if(StopDCS != -1) {
    doStopDCS = "-StopDCS"
    msg.message.Stop.DCS = true
    msg.message.Notification = DDC2.ServerInfo.ServerID+ ` has recieved a `+(Func.Capitalize(msg.message.requesttype))+ ` -DCS command request from `+msg.author.username+ `. Please wait while this task is processed....`
    msg.payload = '-'+msg.message.requesttype+" "+doStopDCS
    return msg
}
////////////////////////////////////////////////////////////////////////////////////////////////////
var StopSRS = msglow.indexOf('-srs')
var doStopSRS = ""
if(StopSRS != -1) {
    doStopSRS = "-StopSRS"
    msg.message.Stop.SRS = true
    msg.message.Notification = DDC2.ServerInfo.ServerID+ ` has recieved a `+(Func.Capitalize(msg.message.requesttype))+ ` -SRS command request from `+msg.author.username+ `. Please wait while this task is processed....`
    msg.payload = '-'+msg.message.requesttype+" "+doStopSRS
    return msg
}
////////////////////////////////////////////////////////////////////////////////////////////////////
//Execution of Default Stop Message
msg.message.Notification = DefaultNote
msg.message.Stop.Game = true
msg.payload = DefaultPayload
return msg

//DO NOT MODIFY THIS DATA!! ALL DATA IS PULLED FROM THE DDC2_CONFIG.ps1 FILE, MODIFY THE DATA THERE!!!!
